func (c *current) onPipeline1(first, rest any) (any, error) {
	out := []DSLNode{first}
	for _, r := range rest.([]any) {
		if r == nil {
			continue
		}
		arr, ok := r.([]any)
		if !ok || len(arr) < 4 || arr[3] == nil {
			continue
		}
		node, ok := arr[3].(DSLNode)
		if !ok {
			continue
		}
		out = append(out, node)
	}
	if len(out) == 1 {
		return out[0], nil
	}
	return &PipelineNode{Stages: out}, nil
}

func (p *parser) callonPipeline1() (any, error) {
	stack := p.vstack[len(p.vstack)-1]
	_ = stack
	return p.cur.onPipeline1(stack["first"], stack["rest"])
}

