func (c *current) onString1(chars any) (any, error) {
	s := ""
	switch v := chars.(type) {
	case string:
		s = v
	case []rune:
		s = string(v)
	case []byte:
		s = string(v)
	case []interface{}:
		for _, c := range v {
			switch x := c.(type) {
			case rune:
				s += string(x)
			case byte:
				s += string(x)
			case string:
				s += x
			case []uint8:
				s += string(x)
			}
		}
	}
	return &LiteralNode{Value: s}, nil
}

